Action.Str:
var loc_qs;
var loc_s;
var lat_lon;
var err;
global('W_mor_title') || setGlobal('W_mor_title', 'Today It Rains');
if (/^city/i.test(global('W_mor_type'))) {
global('W_loc_city') || (err = 1);
loc_qs = 'q=' + global('W_loc_city');
loc_s = global('W_loc_city');
} else {
//auto
if(global('LOCTMS') && global('LOCNTMS')) {
lat_lon = (global('LOCNTMS') > global('LOCTMS') ? global('LOCN') : global('LOC')).split(',',2);
} else {
lat_lon = (global('LOC') || global('LOCN')).split(',',2);
}
(lat_lon[0] && lat_lon[1]) || (err = 1);
loc_qs = 'lat=' + lat_lon[0] + '&lon=' + lat_lon[1];
loc_s = 'your location';
}

var forecast = JSON.parse(global('HTTPD'));
var tend = global('W_mor_hahead') ? (Date.now() / 1000 + 60*60*global('W_mor_hahead')) : (new Date().setHours(23,59,0,0)) / 1000;
var first_rain = forecast.list.map(item => {
return item.dt <= tend && ((item.rain || {})['3h'] > 0 || (item.snow || {})['3h'] > 0);
}).indexOf(true);
var today_rains = first_rain >= 0;
var rain_start;
var rain_start_s;
if (today_rains) {
rain_start = new Date(forecast.list[first_rain].dt * 1000);
rain_start_s = rain_start.getHours() + ':' + ((rain_start.getMinutes() > 9) ? '' : 0) + rain_start.getMinutes();
}

var forecast = JSON.parse(global('HTTPD'));
var tend = global('W_mor_hahead') ? (Date.now() / 1000 + 60*60*global('W_mor_hahead')) : (new Date().setHours(23,59,0,0)) / 1000;
var first_rain = forecast.list.map(item => {
return item.dt <= tend && item.weather.some(i => {return i.id >= 200 && i.id <= 699;});
}).indexOf(true);
var today_rains = first_rain >= 0;
var rain_start;
var rain_start_s;
if (today_rains) {
rain_start = new Date(forecast.list[first_rain].dt * 1000);
rain_start_s = rain_start.getHours() + ':' + ((rain_start.getMinutes() > 9) ? '' : 0) + rain_start.getMinutes();
}
